import "../App.css";
import LoginSign from "../components/loginsign";
import { useEffect, useState } from "react";
import Navbar from "../components/navbar";
import { useContext } from "react";
import { GlobalFunctionContext } from "../context/functionsContext";
import { useNavigate } from "react-router-dom";
import { ThemeContext } from "../context/themeContext";
import { useDispatch } from "react-redux";
import { setCredentials } from "../Slices/authSlice";
import { GoogleLogin } from "@react-oauth/google";
import axios from "axios";
import { jwtDecode } from "jwt-decode";


function Login() {
  const [form, setForm] = useState({ email: "", password: "" });
  const [message, setMessage] = useState("");
  const { handleSignUp, handleAbout } = useContext(GlobalFunctionContext);
  const { theme } = useContext(ThemeContext);

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const dispatch = useDispatch();

  const navigate = useNavigate();

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });
  useEffect(() => {});

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/api/auth/Login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
      credentials: "include",
    });
    const data = await res.json();
    setMessage(data.msg);

    if (res.ok) {
      dispatch(setCredentials({ user: data.user, token: data.token }));
      navigate("/Main", { state: { token: data.token } });
    }
  };

const handleGoogleSuccess = async (credentialResponse) => {
    try {
      const token = credentialResponse.credential;

      // ✅ FIX: Correct endpoint (depends on your backend setup)
      const res = await axios.post("http://localhost:5000/api/auth/google", {
        token,
      });

      // Save your backend JWT, not Google’s raw token
      localStorage.setItem("token", res.data.token);

      // Optionally save in Redux for auth state
      dispatch(setCredentials({ user: res.data, token: res.data.token }));

      // ✅ Use React Router navigation (no reload)
      navigate("/Main", {state: { token:token}});
    } catch (err) {
      console.error("Google login error:", err);
    }
  };

  return (
    <div
      className="loginPage"
      style={{ background: theme.background, color: theme.text }}
    >
      <Navbar onSignUp={handleSignUp} />
      <h1 style={{ textAlign: "center" }}>Login</h1>
      <form onSubmit={handleSubmit}>
        <div className="box">
          <label htmlFor="username">Enter your username</label>
          <input
            name="username"
            placeholder="Username"
            onChange={handleChange}
          />

          <label htmlFor="email">Enter your email</label>
          <input
            type="email"
            name="email"
            placeholder="Email"
            onChange={handleChange}
          />

          <label htmlFor="password">Password</label>
          <input
            type="password"
            name="password"
            placeholder="Password"
            onChange={handleChange}
          />

          <button className="loginButton" type="submit">
            Log in
          </button>
            <div className="login-page">
              <GoogleLogin
                onSuccess={handleGoogleSuccess}
                onError={() => console.log("Error")}
              />
            </div>
          <p>{message}</p>
        </div>
      </form>
    </div>
  );
}

export default Login;
