import React from 'react'
import { useState } from 'react';
import { useContext } from 'react';
import { ProductContext } from '../context/productContext';
function Dashboard() {
    const {products, setProducts} = useContext(ProductContext)

      const [newProduct, setNewProduct] = useState({
        name: "",
        price: "",
        description: "",
        category: "",
        image: "",
      });
   
    
      const handleAddProduct = async () => {
        try {
          const res = await fetch("http://localhost:5000/api/products", {
            method: "POST",
            credentials: "include",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(newProduct),
          });
          const data = await res.json();
          console.log("Product added:", data);
          setProducts((prev) => [...prev, data.product]);
        } catch (err) {
          console.error("Error adding product:", err);
        }
      };
    
  return (
    <div>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            gap: "10px",
            padding: "10px 15px",
            background: "#f0f0f3",
            borderRadius: "12px",
            boxShadow: "4px 4px 10px #d1d1d1, -4px -4px 10px #ffffff",
            fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
            flexWrap: "wrap", // allows wrapping on small screens
          }}
        >
          {["Name", "Price", "Description", "Category", "Image URL"].map(
            (placeholder) => (
              <input
                key={placeholder}
                placeholder={placeholder}
                type={placeholder === "Price" ? "number" : "text"}
                style={{
                  padding: "6px 10px",
                  border: "none",
                  borderRadius: "8px",
                  background: "#e0e0e5",
                  boxShadow:
                    "inset 2px 2px 5px #d1d1d1, inset -2px -2px 5px #ffffff",
                  fontSize: "13px",
                  outline: "none",
                  width: "120px",
                  transition: "all 0.2s ease-in-out",
                }}
                onFocus={(e) =>
                  (e.target.style.boxShadow = "0 0 0 2px #007BFF")
                }
                onBlur={(e) =>
                  (e.target.style.boxShadow =
                    "inset 2px 2px 5px #d1d1d1, inset -2px -2px 5px #ffffff")
                }
                onChange={(e) =>
                  setNewProduct({
                    ...newProduct,
                    [placeholder.toLowerCase().replace(" ", "")]:
                      e.target.value,
                  })
                }
              />
            )
          )}

          <button
            onClick={handleAddProduct}
            style={{
              padding: "8px 12px",
              border: "none",
              borderRadius: "10px",
              background: "linear-gradient(90deg, #007BFF, #0056b3)",
              color: "#fff",
              fontSize: "14px",
              fontWeight: "600",
              cursor: "pointer",
              transition: "all 0.3s ease",
              boxShadow: "2px 2px 5px rgba(0,0,0,0.2)",
              whiteSpace: "nowrap",
            }}
            onMouseEnter={(e) =>
              (e.target.style.background =
                "linear-gradient(90deg, #0056b3, #007BFF)")
            }
            onMouseLeave={(e) =>
              (e.target.style.background =
                "linear-gradient(90deg, #007BFF, #0056b3)")
            }
          >
            Add
          </button>
        </div>
    </div>
  )
}

export default Dashboard
