import "../App.css";
import Myfooter from "../components/footer";
// import { products } from '../constants/c';
import { useLocation, useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import Cookies from "js-cookie";

import {
  useCallback,
  useContext,
  useEffect,
  useMemo,
  useRef,
  useState,
} from "react";
import { LazyLoadImage } from "react-lazy-load-image-component";
import { useDispatch, useSelector } from "react-redux";
import {
  selectProducts,
  fetchProducts,
  selectLoading,
  selectError,
} from "../Slices/productsSlice";
import CustomButton from "../components/button";
import { ProductContext } from "../context/productContext";
import chevron from "../constants/chevron.png";
import searchImg from "../constants/searchImg.png";
import Navbar from "../components/navbar";
import MyMap from "../components/map";
import { GlobalFunctionContext } from "../context/functionsContext";
import { ThemeContext } from "../context/themeContext";
import { logout } from "../Slices/authSlice";

function Main() {
  // const products = useSelector(selectProducts);
      const {products, setProducts} = useContext(ProductContext)
  
  const { user, token } = useSelector((state) => state.auth);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [search, setSearch] = useState("");
  const { setSelectedProduct } = useContext(ProductContext);
  // const [products, setProducts] = useState([]);
  const [loading, setLoading] = use(true);
  const { theme } = useContext(ThemeContext);

  useEffect(() => {
    if (!token) return;
    console.log(token);

    try {
      const decoded = jwtDecode(token.token);
      if (!decoded.exp) return;

      const expiryTime = decoded.exp * 1000;
      console.log(expiryTime);

      const currentTime = Date.now();
      console.log(currentTime);

      if (expiryTime <= currentTime) {
        dispatch(logout());
        navigate("/");
        return;
      }

      const timeout = setTimeout(() => {
        dispatch(logout());
        navigate("/");
      }, expiryTime - currentTime);

      return () => clearTimeout(timeout);
    } catch (err) {
      console.error("Invalid token:", err);
      dispatch(logout());
      navigate("/");
    }
  }, [token, dispatch, navigate]);
  const handleLogout = async () => {
    try {
      await fetch("http://localhost:5000/Logout", {
        method: "POST",
        credentials: "include", // make sure cookie is included
      });

      dispatch(logout()); // clear Redux state
      navigate("/"); // redirect to login or home
    } catch (err) {
      console.error("Logout failed:", err);
    }
  };

  const { selectedProduct: product } = useContext(ProductContext);
  const {
    navBarRef,
    handleLogin,
    handleSignUp,
    handleAbout,
    scrollToContact,
    scrollToUp,
    contactRef,
  } = useContext(GlobalFunctionContext);

  // function useAuthCheck() {

  // useEffect(() => {
  //   const token = Cookies.get("token");
  //   if (!token) {
  //     navigate("/");
  //   }
  // }, [navigate]);

  // const dispatch = useDispatch();
  // const loading = useSelector(selectLoading);
  // const error = useSelector(selectError);

  // const fetchData = async () => {
  //   try {
  //     const res = await fetch("https://fakestoreapi.in/api/products")
  //     const data = await res.json()

  //     if (JSON.stringify(data.products) !== JSON.stringify(products)) {
  //       setProducts(data.products)
  //     }
  //     setLoading(false)
  //   }
  //   catch (err) {
  //     console.log("Failed to fetch products:", err);
  //     setLoading(false)
  //   }

  // }

  // fetchData()

  // useEffect(() => {
  //   dispatch(fetchProducts());
  // }, [dispatch])

  const [newProduct, setNewProduct] = useState({
    name: "",
    price: "",
    description: "",
    category: "",
    image: "",
  });
  const handleDeleteProduct = async (id) => {
    try {
      await fetch(`http://localhost:5000/api/products/${id}`, {
        method: "DELETE",
        credentials: "include",
      });
      setProducts((prev) => prev.filter((product) => product._id !== id));
    } catch (err) {
      console.error("Error deleting product:", err);
    }
  };


  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/products", {
          credentials: "include",
        });
        const data = await res.json();
        setProducts(data);
        setLoading(false);
      } catch (err) {
        console.error("Failed to fetch products", err);
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const filteredProducts = useMemo(() => {
    return products.filter(
      (item) =>
        item.name.toLowerCase().includes(search.toLowerCase()) ||
        item.category.toLowerCase().includes(search.toLowerCase())
    );
  }, [search, products]);

  const handleProducts = (item) => {
    setSelectedProduct(item);
    navigate("/productPage");
  };

  // useEffect(() => {
  //   const checkAuth = async () => {
  //     try {
  //       const res = await fetchWithAuth(
  //         "http://localhost:5000/api/products",
  //         {},
  //         navigate
  //       );
  //       if (!res) return; // means logged out;

  //       if (res.status === 401) {
  //         navigate("/");
  //       }
  //     } catch (err) {
  //       console.error("Error checking auth:", err);
  //       navigate("/");
  //     }
  //   };

  return (
    <div
      className="app"
      style={{ background: theme.background, color: theme.text }}
    >
      <Navbar
        navRef={navBarRef}
        onLogin={handleLogin}
        onSignUp={handleSignUp}
        onAbout={handleAbout}
        onContact={scrollToContact}
      />
      <div>
        <button
          onClick={handleLogout}
          style={{
            padding: "8px 14px",
            background: "crimson",
            color: "white",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
          }}
        >
          Logout
        </button>
      </div>

      <div className="content">
        <h2>Products for Sale</h2>
        <div>
          <img className="searchImg" src={searchImg} alt="noimage" />
          <input
            type="search"
            className="search"
            name="search"
            id="search"
            placeholder="search anything"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </div>

        <div className="products">
          {loading ? (
            <div className="spinner"></div>
          ) : (
            filteredProducts.map((item) => (
              <div
                key={item.id}
                onClick={() => handleProducts(item)}
                className={`card ${item.discount > 10 ? "highlight" : ""}`}
              >
                <LazyLoadImage
                  className="imgProduct"
                  src={item.image}
                  alt="image cannot be found"
                />
                <a>
                  <h4> {item.name}</h4>
                </a>
                <strong>Price: ${item.price.toLocaleString()}</strong>
                {/* <p>Rating: {item.rating}</p> */}
                <div className="details">
                  <pre>Description: {item.description}</pre>
                  {/* <pre>Model: {item.model}</pre> */}
                  {/* <pre>Color: {item.color}</pre> */}
                  <pre>Category: {item.category}</pre>
                  {/* <pre>Sale: {item.onSale}</pre>
                  <pre>Discount: {item.discount}%</pre> */}
                </div>
                <button
                  className="btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDeleteProduct(item._id);
                  }}
                >
                  Delete
                </button>
              </div>
            ))
          )}
          <div onClick={scrollToUp}>
            <img className="img" src={chevron} alt="noimage" />
          </div>
        </div>
        <MyMap />
      </div>
      <Myfooter ref={contactRef} />
    </div>
  );
}

export default Main;
