import React from 'react'
import '../App.css';
import { useNavigate } from 'react-router-dom';
import CustomButton from '../components/button';
import { useContext } from 'react';
import { ThemeContext } from '../context/themeContext';


const Navbar = ({onLogin, onSignUp, onAbout, onContact, navRef}) => {

  const { theme, toggleTheme, isDark } = useContext(ThemeContext);
  const navigate = useNavigate()
  
  return (
    <div ref={navRef} className="navbar">
      <div className="brand">
        <a onClick={() => navigate('/')}><img className='logo' src="/logo192.png" alt="no-image" /></a>
        <h3>React Commerce</h3>
      </div>
      <ul className="pages">
        <li onClick={() => navigate('/')}><a>Home</a></li>
        <li onClick={onContact}><a>Contact</a></li>
        <li onClick={onAbout}><a>About</a></li>
        <li><CustomButton label="Login" onClick={onLogin} /></li>
        <li><CustomButton label="Sign Up" onClick={onSignUp} /></li>
      </ul>
      <button
        onClick={toggleTheme}
        style={{
          background: theme.buttonBg,
          color: theme.buttonText,
          border: "none",
          // padding: "8px 16px",
          borderRadius: "5px",
          cursor: "pointer"
        }}
      >
        {isDark ? "Light Mode" : "Dark Mode"}
      </button>
    </div>
  )
}

export default React.memo(Navbar);
