import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import Navbar from "../components/navbar";

function AdminDashboard() {
  const { token, user } = useSelector((state) => state.auth);

  const [products, setProducts] = useState([]);
  const [users, setUsers] = useState([]);
  const [orders, setOrders] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: "",
    price: "",
    description: "",
    category: "",
    image: "",
    quantity: 1,
  });

  // Only allow access if user is admin or sub-admin
  if (!user || (user.role !== "admin" && user.role !== "subadmin")) {
    return <p style={{ textAlign: "center" }}>Access Denied</p>;
  }

  /** FETCH DATA **/
  useEffect(() => {
    // Fetch products
    fetch("http://localhost:5000/api/products", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setProducts(data);
        } else {
          console.error("Products API did not return an array:", data);
          setProducts([]);
        }
      })
      .catch((err) => console.error("Products fetch failed:", err));

    // Fetch users (admin only)
    if (user.role === "admin") {
      fetch("http://localhost:5000/api/users", {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => res.json())
        .then((data) => {
          if (Array.isArray(data)) {
            setUsers(data);
          } else {
            console.error("Users API did not return an array:", data);
            setUsers([]);
          }
        })
        .catch((err) => console.error("Users fetch failed:", err));
    }

    // Fetch orders
    fetch("http://localhost:5000/api/orders", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setOrders(data);
        } else {
          console.error("Orders API did not return an array:", data);
          setOrders([]);
        }
      })
      .catch((err) => console.error("Orders fetch failed:", err));
  }, [token, user.role]);

  /** HANDLE ADD PRODUCT **/
  const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newProduct),
      });
      const data = await res.json();
      if (res.ok) {
        setProducts((prev) => [...prev, data.product || data]);
      }
      setNewProduct({
        name: "",
        price: "",
        description: "",
        category: "",
        image: "",
        quantity: 1,
      });
    } catch (err) {
      console.error("Add product failed:", err);
    }
  };

  /** HANDLE DELETE PRODUCT **/
  const handleDeleteProduct = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/products/${id}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.ok) {
        setProducts(products.filter((p) => p._id !== id));
      }
    } catch (err) {
      console.error("Delete product failed:", err);
    }
  };

  /** HANDLE UPDATE QUANTITY **/
  const handleQuantityUpdate = async (id, quantity) => {
    try {
      const res = await fetch(`http://localhost:5000/api/products/${id}/quantity`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ quantity }),
      });
      if (res.ok) {
        setProducts(products.map((p) => (p._id === id ? { ...p, quantity } : p)));
      }
    } catch (err) {
      console.error("Update quantity failed:", err);
    }
  };

  /** HANDLE ASSIGN ROLE **/
  const handleAssignRole = async (userId, role) => {
    try {
      const res = await fetch(`http://localhost:5000/api/users/${userId}/role`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ role }),
      });
      if (res.ok) {
        setUsers(users.map((u) => (u._id === userId ? { ...u, role } : u)));
      }
    } catch (err) {
      console.error("Assign role failed:", err);
    }
  };

  return (
    <div style={{ minHeight: "100vh", background: "#f8f9fb" }}>
      <Navbar />

      <div style={{ padding: "30px" }}>
        <h2>📦 Products</h2>
        <form onSubmit={handleAddProduct} style={{ marginBottom: "20px" }}>
          <input
            type="text"
            placeholder="Name"
            value={newProduct.name}
            onChange={(e) =>
              setNewProduct({ ...newProduct, name: e.target.value })
            }
            required
          />
          <input
            type="number"
            placeholder="Price"
            value={newProduct.price}
            onChange={(e) =>
              setNewProduct({ ...newProduct, price: e.target.value })
            }
            required
          />
          <input
            type="text"
            placeholder="Category"
            value={newProduct.category}
            onChange={(e) =>
              setNewProduct({ ...newProduct, category: e.target.value })
            }
            required
          />
          <input
            type="text"
            placeholder="Image URL"
            value={newProduct.image}
            onChange={(e) =>
              setNewProduct({ ...newProduct, image: e.target.value })
            }
          />
          <input
            type="number"
            placeholder="Quantity"
            value={newProduct.quantity}
            onChange={(e) =>
              setNewProduct({ ...newProduct, quantity: e.target.value })
            }
            min={1}
          />
          <button type="submit">➕ Add Product</button>
        </form>

        <ul>
          {Array.isArray(products) &&
            products.map((p) => (
              <li key={p._id}>
                {p.name} - ${p.price} - Quantity:{" "}
                <input
                  type="number"
                  defaultValue={p.quantity || 0}
                  onBlur={(e) => handleQuantityUpdate(p._id, e.target.value)}
                  disabled={user.role === "subadmin" ? false : false}
                />
                <button
                  onClick={() => handleDeleteProduct(p._id)}
                  disabled={user.role === "subadmin"} // Sub-admin cannot delete
                >
                  Delete
                </button>
              </li>
            ))}
        </ul>

        {user.role === "admin" && (
          <>
            <h2>👥 Users</h2>
            <ul>
              {Array.isArray(users) &&
                users.map((u) => (
                  <li key={u._id}>
                    {u.name || u.email} ({u.role})
                    <select
                      value={u.role}
                      onChange={(e) => handleAssignRole(u._id, e.target.value)}
                    >
                      <option value="user">User</option>
                      <option value="subadmin">Sub-admin</option>
                      <option value="admin">Admin</option>
                    </select>
                  </li>
                ))}
            </ul>
          </>
        )}

        <h2>🛒 Orders</h2>
        <ul>
          {Array.isArray(orders) &&
            orders.map((o) => (
              <li key={o._id}>
                Order by: {o.user?.name || "Unknown"} | Total: ${o.total} | Items:{" "}
                {o.items?.map((i) => i.name).join(", ")}
              </li>
            ))}
        </ul>
      </div>
    </div>
  );
}

export default AdminDashboard;
