import "../App.css";
import Myfooter from "../components/footer";
// import { products } from '../constants/c';
import { useNavigate } from "react-router-dom";
import {
  useCallback,
  useContext,
  useEffect,
  useMemo,
  useRef,
  useState,
} from "react";
import { LazyLoadImage } from "react-lazy-load-image-component";
import { useDispatch, useSelector } from "react-redux";
import {
  selectProducts,
  fetchProducts,
  selectLoading,
  selectError,
} from "../Slices/productsSlice";
import CustomButton from "../components/button";
import { ProductContext } from "../context/productContext";
import chevron from "../constants/chevron.png";
import searchImg from "../constants/searchImg.png";
import Navbar from "../components/navbar";
import MapComponent from "../components/mapComponent";
import MyMap from "../components/map";
import { GlobalFunctionContext } from "../context/functionsContext";

function Main() {
  // const products = useSelector(selectProducts);
  const [search, setSearch] = useState("");

  const { selectedProduct: product } = useContext(ProductContext);
  const {
    navBarRef,
    handleLogin,
    handleSignUp,
    handleAbout,
    scrollToContact,
    scrollToUp,
    contactRef,
  } = useContext(GlobalFunctionContext);

  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  const navigate = useNavigate();

  // const dispatch = useDispatch();
  // const loading = useSelector(selectLoading);
  // const error = useSelector(selectError);

  // const fetchData = async () => {
  //   try {
  //     const res = await fetch("https://fakestoreapi.in/api/products")
  //     const data = await res.json()

  //     if (JSON.stringify(data.products) !== JSON.stringify(products)) {
  //       setProducts(data.products)
  //     }
  //     setLoading(false)
  //   }
  //   catch (err) {
  //     console.log("Failed to fetch products:", err);
  //     setLoading(false)
  //   }

  // }

  // fetchData()

  // useEffect(() => {
  //   dispatch(fetchProducts());
  // }, [dispatch]);

  const [newProduct, setNewProduct] = useState({
    name: "",
    price: "",
    description: "",
    category: "",
    image: "",
  });
  const handleDeleteProduct = async (id) => {
    try {
      await fetch(`http://localhost:5000/api/products/${id}`, {
        method: "DELETE",
        credentials: "include",
      });
      setProducts((prev) => prev.filter((product) => product._id !== id));
    } catch (err) {
      console.error("Error deleting product:", err);
    }
  };

  const handleAddProduct = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        credentials: "include",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newProduct),
      });
      const data = await res.json();
      console.log("Product added:", data);
      setProducts((prev) => [...prev, data.product]);
    } catch (err) {
      console.error("Error adding product:", err);
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/products", {
          credentials: "include",
        });
        const data = await res.json();
        setProducts(data);
        setLoading(false);
      } catch (err) {
        console.error("Failed to fetch products", err);
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const filteredProducts = useMemo(() => {
    return products.filter((item) =>
      item.title.toLowerCase().includes(search.toLowerCase())
    );
  }, [search, products]);

  const { setSelectedProduct } = useContext(ProductContext);

  const handleProducts = (item) => {
    setSelectedProduct(item);
    navigate("/productPage");
  };

  useEffect(() => {
    const checkAuth = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/auth/main", {
          credentials: "include",
        });

        if (res.status === 401) {
          navigate("/");
        }
      } catch (err) {
        console.error("Error checking auth:", err);
        navigate("/");
      }
    };

    checkAuth();
    const interval = setInterval(checkAuth, 2000);
    return () => clearInterval(interval);
  }, [navigate]);

  return (
    <div className="app">
      <Navbar
        navRef={navBarRef}
        onLogin={handleLogin}
        onSignUp={handleSignUp}
        onAbout={handleAbout}
        onContact={scrollToContact}
      />
      <div>
       <div
  style={{
    maxWidth: "350px",
    margin: "30px auto",
    padding: "15px",
    background: "#f9f9f9",
    borderRadius: "8px",
    boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
    display: "flex",
    flexDirection: "column",
    gap: "10px",
  }}
>
  <h3 style={{ textAlign: "center", margin: "0", color: "#333" }}>Add Product</h3>

  <input
    placeholder="Name"
    style={{ padding: "8px", border: "1px solid #ccc", borderRadius: "5px", fontSize: "14px" }}
    onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
  />
  <input
    placeholder="Price"
    type="number"
    style={{ padding: "8px", border: "1px solid #ccc", borderRadius: "5px", fontSize: "14px" }}
    onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
  />
  <input
    placeholder="Description"
    style={{ padding: "8px", border: "1px solid #ccc", borderRadius: "5px", fontSize: "14px" }}
    onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
  />
  <input
    placeholder="Category"
    style={{ padding: "8px", border: "1px solid #ccc", borderRadius: "5px", fontSize: "14px" }}
    onChange={(e) => setNewProduct({ ...newProduct, category: e.target.value })}
  />
  <input
    placeholder="Image URL"
    style={{ padding: "8px", border: "1px solid #ccc", borderRadius: "5px", fontSize: "14px" }}
    onChange={(e) => setNewProduct({ ...newProduct, image: e.target.value })}
  />

  <button
    onClick={handleAddProduct}
    style={{
      padding: "10px",
      backgroundColor: "#007BFF",
      color: "#fff",
      fontSize: "14px",
      border: "none",
      borderRadius: "5px",
      cursor: "pointer",
    }}
  >
    Add Product
  </button>
</div>

      </div>

      <div className="content">
        <h2>Products for Sale</h2>
        <div>
          <img className="searchImg" src={searchImg} alt="noimage" />
          <input
            type="search"
            className="search"
            name="search"
            id="search"
            placeholder="search anything"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </div>

        <div className="products">
          {loading ? (
            <div className="spinner"></div>
          ) : (
            products.map((item) => (
              <div
                key={item.id}
                onClick={() => handleProducts(item)}
                className={`card ${item.discount > 10 ? "highlight" : ""}`}
              >
                <LazyLoadImage
                  className="imgProduct"
                  src={item.image}
                  alt="image cannot be found"
                />
                <a>
                  <h4> {item.name}</h4>
                </a>
                <strong>Price: ${item.price.toLocaleString()}</strong>
                <p>Rating: {item.rating}</p>
                <div className="details">
                  <pre>Brand: {item.description}</pre>
                  {/* <pre>Model: {item.model}</pre> */}
                  {/* <pre>Color: {item.color}</pre> */}
                  <pre>Category: {item.category}</pre>
                  {/* <pre>Sale: {item.onSale}</pre>
                  <pre>Discount: {item.discount}%</pre> */}
                </div>
                <button
                  className="btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDeleteProduct(item._id);
                  }}
                >
                  Delete
                </button>
              </div>
            ))
          )}
          <div onClick={scrollToUp}>
            <img className="img" src={chevron} alt="noimage" />
          </div>
        </div>
        <MyMap />
      </div>
      <Myfooter ref={contactRef} />
    </div>
  );
}

export default Main;
