import React, { useContext } from "react";
import "../App.css";
import { useNavigate } from "react-router-dom";
import CustomButton from "../components/button";
import { useSelector } from "react-redux";
import { ThemeContext } from "../context/themeContext";
import cart from "../constants/cart.png";

const Navbar = ({ onLogin, onSignUp, onAbout, onContact, navRef }) => {
  const { theme, toggleTheme, isDark } = useContext(ThemeContext);
  const navigate = useNavigate();
  const { user, token } = useSelector((state) => state.auth); // ðŸ‘ˆ get auth info

  return (
    <div
      ref={navRef}
      className="navbar"
      style={{ background: theme.navbarBg, color: theme.text }}
    >
      <div className="brand">
        <a onClick={() => navigate("/")}>
          <img
            style={{ cursor: "pointer" }}
            className="logo"
            src="/logo192.png"
            alt="logo"
          />
        </a>
        <h3>React Commerce</h3>
      </div>

      <ul className="pages">
        <li onClick={() => navigate("/")}>
          <a>Home</a>
        </li>

        {/* Show Dashboard only if Admin */}
        {user?.role === "admin" && (
          <li onClick={() => navigate("/admin")}>
            <a>Admin Dashboard</a>
          </li>
        )}

        <li onClick={onContact}>
          <a>Contact</a>
        </li>
        <li onClick={onAbout}>
          <a>About</a>
        </li>

        {/* Normal users still see cart */}
        {user?.role !== "admin" && (
          <li>
            <img
              onClick={() => navigate("/CartScreen")}
              style={{ height: "30px", width: "30px" }}
              src={cart}
              alt="cart"
            />
          </li>
        )}

        {/* Show Login/Signup if not logged in */}
        {!token ? (
          <>
            <li>
              <CustomButton label="Login" onClick={onLogin} />
            </li>
            <li>
              <CustomButton label="Sign Up" onClick={onSignUp} />
            </li>
          </>
        ) : (
          <li>
            <CustomButton
              label="Logout"
              onClick={() => {
                localStorage.removeItem("token");
                window.location.href = "/login";
              }}
            />
          </li>
        )}

        <button
          onClick={toggleTheme}
          className="btn"
          style={{
            background: theme.buttonBg,
            color: theme.buttonText,
            marginLeft: "30px",
          }}
        >
          {isDark ? "Light Mode" : "Dark Mode"}
        </button>
      </ul>
    </div>
  );
};

export default React.memo(Navbar);
