import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import AdminNavbar from "../components/adminNavbar";
import { logout } from "../Slices/authSlice";
import { useLocation, useNavigate } from "react-router-dom";
import "./AdminDashboard.css"; 

function AdminDashboard() {
  const { token, user } = useSelector((state) => state.auth);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const location = useLocation();

  const [products, setProducts] = useState([]);
  const [users, setUsers] = useState([]);
  const [orders, setOrders] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: "",
    price: "",
    description: "",
    category: "",
    image: "",
    quantity: 1,
  });

  const handleLogout = () => {
    dispatch(logout());
    window.location.href = "/";
  };

  useEffect(() => {
    fetch("http://localhost:5000/api/products", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setProducts(Array.isArray(data) ? data : []))
      .catch((err) => console.error("Products fetch failed:", err));

    if (user.role === "admin") {
      fetch("http://localhost:5000/api/users", {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => res.json())
        .then((data) => setUsers(Array.isArray(data) ? data : []))
        .catch((err) => console.error("Users fetch failed:", err));
    }

    fetch("http://localhost:5000/api/orders", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setOrders(Array.isArray(data) ? data : []))
      .catch((err) => console.error("Orders fetch failed:", err));
  }, [token, user.role]);

  const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newProduct),
      });
      const data = await res.json();
      if (res.ok) {
        setProducts((prev) => [...prev, data.product || data]);
      }
      setNewProduct({
        name: "",
        price: "",
        description: "",
        category: "",
        image: "",
        quantity: 1,
      });
    } catch (err) {
      console.error("Add product failed:", err);
    }
  };

  const handleDeleteProduct = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/products/${id}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.ok) {
        setProducts(products.filter((p) => p._id !== id));
      }
    } catch (err) {
      console.error("Delete product failed:", err);
    }
  };

  const handleQuantityUpdate = async (id, quantity) => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/products/${id}/quantity`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ quantity }),
        }
      );
      if (res.ok) {
        setProducts(
          products.map((p) => (p._id === id ? { ...p, quantity } : p))
        );
      }
    } catch (err) {
      console.error("Update quantity failed:", err);
    }
  };

  const handleAssignRole = async (userId, role) => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/users/${userId}/role`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ role }),
        }
      );
      if (res.ok) {
        setUsers(users.map((u) => (u._id === userId ? { ...u, role } : u)));
      }
    } catch (err) {
      console.error("Assign role failed:", err);
    }
  };

  useEffect(() => {
    const handlePopState = () => {
      dispatch(logout());
      navigate("/");
    };

    window.addEventListener("popstate", handlePopState);
    return () => window.removeEventListener("popstate", handlePopState);
  }, [navigate, location]);

  if (!user || (user.role !== "admin" && user.role !== "subadmin")) {
    return <p className="denied">ðŸš« Access Denied</p>;
  }

  return (
    <div className="dashboard-container">
      <AdminNavbar />
      <div className="dashboard-content">
        {/* Products */}
        <section className="card">
          <h2>ðŸ“¦ Products</h2>
          <form className="product-form" onSubmit={handleAddProduct}>
            <input
              type="text"
              placeholder="Name"
              value={newProduct.name}
              onChange={(e) =>
                setNewProduct({ ...newProduct, name: e.target.value })
              }
              required
            />
            <input
              type="number"
              placeholder="Price"
              value={newProduct.price}
              onChange={(e) =>
                setNewProduct({ ...newProduct, price: e.target.value })
              }
              required
            />
            <input
              type="text"
              placeholder="Category"
              value={newProduct.category}
              onChange={(e) =>
                setNewProduct({ ...newProduct, category: e.target.value })
              }
              required
            />
            <input
              type="text"
              placeholder="Image URL"
              value={newProduct.image}
              onChange={(e) =>
                setNewProduct({ ...newProduct, image: e.target.value })
              }
            />
            <input
              type="number"
              placeholder="Quantity"
              value={newProduct.quantity}
              onChange={(e) =>
                setNewProduct({ ...newProduct, quantity: e.target.value })
              }
              min={1}
            />
            <button type="submit" className="btn-primary">
              âž• Add Product
            </button>
          </form>

          <table className="styled-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Price ($)</th>
                <th>Category</th>
                <th>Quantity</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {products.map((p) => (
                <tr key={p._id}>
                  <td>{p.name}</td>
                  <td>{p.price}</td>
                  <td>{p.category}</td>
                  <td>
                    <input
                      type="number"
                      defaultValue={p.quantity || 0}
                      onBlur={(e) =>
                        handleQuantityUpdate(p._id, e.target.value)
                      }
                    />
                  </td>
                  <td>
                    <button
                      onClick={() => handleDeleteProduct(p._id)}
                      className="btn-danger"
                      disabled={user.role === "subadmin"}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>

        {/* Users */}
        {user.role === "admin" && (
          <section className="card">
            <h2>ðŸ‘¥ Users</h2>
            <table className="styled-table">
              <thead>
                <tr>
                  <th>Name/Email</th>
                  <th>Role</th>
                  <th>Assign Role</th>
                </tr>
              </thead>
              <tbody>
                {users.map((u) => (
                  <tr key={u._id}>
                    <td>{u.name || u.email}</td>
                    <td>{u.role}</td>
                    <td>
                      <select
                        value={u.role}
                        onChange={(e) =>
                          handleAssignRole(u._id, e.target.value)
                        }
                      >
                        <option value="user">User</option>
                        <option value="subadmin">Sub-admin</option>
                        <option value="admin">Admin</option>
                      </select>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </section>
        )}

        {/* Orders */}
        <section className="card">
          <h2>ðŸ›’ Orders</h2>
          <table className="styled-table">
            <thead>
              <tr>
                <th>User</th>
                <th>Total ($)</th>
                <th>Items</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((o) => (
                <tr key={o._id}>
                  <td>{o.user?.name || "Unknown"}</td>
                  <td>{o.total}</td>
                  <td>{o.items?.map((i) => i.name).join(", ")}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>
      </div>
    </div>
  );
}

export default AdminDashboard;
