// src/Slices/cartSlice.js
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import {
  fetchCart,
  addToCartAPI,
  removeFromCartAPI,
  decreaseQuantityAPI,
  clearCartAPI,
} from "../services/cartService";

// ðŸ”¹ Thunks to communicate with backend
export const loadCart = createAsyncThunk("cart/load", async (userId) => {
  return await fetchCart(userId);
});

export const addToCartBackend = createAsyncThunk(
  "cart/add",
  async ({ userId, productId }) => {
    return await addToCartAPI(userId, productId);
  }
);

export const removeFromCartBackend = createAsyncThunk(
  "cart/remove",
  async ({ userId, productId }) => {
    return await removeFromCartAPI(userId, productId);
  }
);

export const decreaseQuantityBackend = createAsyncThunk(
  "cart/decrease",
  async ({ userId, productId }) => {
    return await decreaseQuantityAPI(userId, productId);
  }
);

export const clearCartBackend = createAsyncThunk("cart/clear", async (userId) => {
  return await clearCartAPI(userId);
});

// ðŸ”¹ Slice
const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],   // keep items in sync with backend
    loading: false,
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      // Load cart
      .addCase(loadCart.pending, (state) => {
        state.loading = true;
      })
      .addCase(loadCart.fulfilled, (state, action) => {
        state.items = action.payload.items;
        state.loading = false;
      })
      .addCase(loadCart.rejected, (state, action) => {
        state.error = action.error.message;
        state.loading = false;
      })

      // Add to cart
      .addCase(addToCartBackend.fulfilled, (state, action) => {
        state.items = action.payload.items;
      })

      .addCase(removeFromCartBackend.fulfilled, (state, action) => {
        state.items = action.payload.items;
      })

      .addCase(decreaseQuantityBackend.fulfilled, (state, action) => {
        state.items = action.payload.items;
      })

      .addCase(clearCartBackend.fulfilled, (state) => {
        state.items = [];
      });
  },
});

export const selectCart = (state) => state.cart.items;

export default cartSlice.reducer;
