import '../App.css';
import Myfooter from '../components/footer';
// import { products } from '../constants/c';
import { useNavigate } from 'react-router-dom';
import { useContext, useEffect, useRef, useState } from 'react';
import { LazyLoadImage } from 'react-lazy-load-image-component';
import { useDispatch, useSelector } from 'react-redux';
import { selectProducts, fetchProducts, selectLoading, selectError } from '../Slices/productsSlice';
import CustomButton from '../components/button';
import { ProductContext } from '../context/useContext';

function Main() {
  const products = useSelector(selectProducts);
  // const [products, setProducts] = useState([]);
  // const [loading, setLoading] = useState(true)

  const navigate = useNavigate()
  const contactRef = useRef(null)
  const dispatch = useDispatch()
  const loading = useSelector(selectLoading)
  const error = useSelector(selectError)


  const postProduct = async (newProduct) => {
    try {
      const response = await fetch("https://fakestoreapi.in/api/products", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newProduct),
      });

      const data = await response.json();
      console.log("Product added:", data);
    } catch (error) {
      console.error("Error posting product:", error);
    }
  };
  // postProduct()


  // const fetchData = async () => {
  //   try {
  //     const res = await fetch("https://fakestoreapi.in/api/products")
  //     const data = await res.json()

  //     if (JSON.stringify(data.products) !== JSON.stringify(products)) {
  //       setProducts(data.products)
  //     }
  //     setLoading(false)
  //   }
  //   catch (err) {
  //     console.log("Failed to fetch products:", err);
  //     setLoading(false)
  //   }

  // }

  // fetchData()

  useEffect(() => {
    dispatch(fetchProducts())
  }, [dispatch])


  const scrollToContact = () => {
    contactRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleLogin = () => {
    navigate('/Login')
  }
  const handleSignUp = () => {
    navigate('/SignUp')
  }
  const handleAbout = () => {
    navigate('/About')
  }

  const {selectedProduct} = useContext(ProductContext)
  const handleProducts = (item) => {
    setSelectedP
    navigate('/productPage', { state: { product: item } })
  }




  return (
    <div className='app'>
      <div className="navbar">
        <div className="brand">
          <a onClick={() => navigate('/')}><img className='logo' src="/logo192.png" alt="no-image" /></a>
          <h3>React Commerce</h3>
        </div>
        <ul className="pages">
          <li onClick={() => navigate('/')}><a>Home</a></li>
          <li onClick={scrollToContact}><a>Contact</a></li>
          <li onClick={handleAbout}><a>About</a></li>
          <li><CustomButton label="Login" onClick={handleLogin}/></li>
          <li><CustomButton label="Sign Up" onClick={handleSignUp} /></li>
        </ul>
      </div>
      <div className="content">
        <h2>Products for Sale</h2>
        <div className='products'>
          {

            loading ? (
              <div className="spinner"></div>

            ) : (products.map(item =>
              <div key={item.id} onClick={() => handleProducts(item)} className="card">
                <LazyLoadImage className='imgProduct' src={item.image} alt="image cannot be found" />              
                <a><h4> {item.title}</h4></a>
                <strong>Price: {item.price.toLocaleString()}</strong>
                <p>Rating: {item.rating}</p>
                <pre className='desc'>{item.brand}</pre>
                <pre className='desc'>{item.model}</pre>
                <pre className='desc'>{item.color}</pre>
                <pre className='desc'>{item.category}</pre>
                <pre className='desc'>{item.onSale}</pre>
                <pre className='desc'>{item.discount}</pre>
              </div>
            ))
          }

        </div>
      </div>
      <Myfooter ref={contactRef} />
    </div>


  );
}

export default Main;
