import React, { useState, useContext, useEffect } from "react";
import { ProductContext } from "../context/productContext";
import { useDispatch, useSelector } from "react-redux";
import { addProduct } from "../Slices/productsSlice";
import Navbar from "../components/navbar";
function Dashboard() {
  const { token, user } = useSelector((state) => state.auth);

  const [products, setProducts] = useState([]);
  const [users, setUsers] = useState([]);
  const [orders, setOrders] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: "",
    price: "",
    description: "",
    category: "",
    image: "",
    quantity: 1,
  });
  // const { setProducts } = useContext(ProductContext);

  const dispatch = useDispatch();

  useEffect(() => {
    fetch("http://localhost:5000/api/products", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then(setProducts);

    if (user.role === "admin") {
      fetch("http://localhost:5000/api/users", {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => res.json())
        .then(setUsers);
    }

    fetch("http://localhost:5000/api/orders", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then(setOrders);
  }, [token, user.role]);

  const handleAddProduct = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        headers: {
          "Content-Type": "applications/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newProduct),
      });
      const data = await res.json();
      if (res.ok) setProducts((prev) => [...prev, data]);
      setNewProduct({
        name: "",
        price: "",
        description: "",
        category: "",
        image: "",
        quantity: 1,
      });
    } catch (err) {
      console.error("Add product failed", err);
    }
  };

  const handleDeleteProduct = async (id) => {
    await fetch(`http://localhost:5000/api/products/${id}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setProducts(products.filter((p) => p._id !== id));
  };

  const handleQuantityUpdate = async (id, quantity) => {
    await fetch(`http://localhost:5000/api/products/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}
      `,
      },
      body: JSON.stringify(quantity),
    });
    setNewProduct(products.map((p) => (p._id === id ? { ...p, quantity } : p)));
  };

  const handleAssignRole = async (userId, role) => {
    await fetch(`http://localhost:5000/api/users/${userId}/role`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ role }),
    });
    setUsers(users.map((u) => (u._id === userId ? { ...u, role } : u)));
  };

  if (!user || (user.role !== "admin" && user.role !== "subadmin")) {
    return <p style={{ textAlign: "center" }}>Access Denied</p>;
  }

  return (
    <div style={{ minHeight: "100vh", background: "#f8f9fb" }}>
      <Navbar />

      <div style={{ padding: "30px" }}>
        <h2>ðŸ“¦ Products</h2>
        <form onSubmit={handleAddProduct} style={{ marginBottom: "20px" }}>
          <input
            type="text"
            placeholder="Name"
            value={newProduct.name}
            onChange={(e) =>
              setNewProduct({ ...newProduct, name: e.target.value })
            }
            required
          />
          <input
            type="number"
            placeholder="Price"
            value={newProduct.price}
            onChange={(e) =>
              setNewProduct({ ...newProduct, price: e.target.value })
            }
            required
          />
          <input
            type="text"
            placeholder="Category"
            value={newProduct.category}
            onChange={(e) =>
              setNewProduct({ ...newProduct, category: e.target.value })
            }
            required
          />
          <input
            type="text"
            placeholder="Image URL"
            value={newProduct.image}
            onChange={(e) =>
              setNewProduct({ ...newProduct, image: e.target.value })
            }
          />
          <input
            type="number"
            placeholder="Quantity"
            value={newProduct.quantity}
            onChange={(e) =>
              setNewProduct({ ...newProduct, quantity: e.target.value })
            }
            min={1}
          />
          <button type="submit">Add Product</button>
        </form>

        <ul>
          {products.map((p) => (
            <li key={p._id}>
              {p.name} - ${p.price} - Quantity:{" "}
              <input
                type="number"
                defaultValue={p.quantity || 0}
                onBlur={(e) => handleQuantityUpdate(p._id, e.target.value)}
                disabled={user.role === "subadmin" ? false : false}
              />
              <button
                onClick={() => handleDeleteProduct(p._id)}
                disabled={user.role === "subadmin"}
              >
                Delete
              </button>
            </li>
          ))}
        </ul>

        {user.role === "admin" && (
          <>
            <h2>ðŸ‘¥ Users</h2>
            <ul>
              {users.map((u) => (
                <li key={u._id}>
                  {u.name} ({u.role})
                  <select
                    value={u.role}
                    onChange={(e) => handleAssignRole(u._id, e.target.value)}
                  >
                    <option value="user">User</option>
                    <option value="subadmin">Sub-admin</option>
                    <option value="admin">Admin</option>
                  </select>
                </li>
              ))}
            </ul>
          </>
        )}

        <h2>ðŸ›’ Orders</h2>
        <ul>
          {orders.map((o) => (
            <li key={o._id}>
              Order by: {o.user.name} | Total: ${o.total} | Items:{" "}
              {o.items.map((i) => i.name).join(", ")}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Dashboard;
