import '../App.css';
import LoginSign from '../components/loginsign';
import { useState } from 'react';
import Navbar from '../components/navbar';
import { useContext } from 'react';
import { GlobalFunctionContext } from '../context/functionsContext';
import { useNavigate } from 'react-router-dom';
import { useEffect } from 'react';
function Login() {
  const [form, setForm] = useState({ email: "", password: "" });
  const [message, setMessage] = useState("");
  const { handleSignUp, handleAbout } = useContext(GlobalFunctionContext)

  const navigate = useNavigate();
  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/api/auth/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
      credentials: "include"
    });
    const data = await res.json();
    setMessage(data.msg);
  
  

  };
   useEffect(() => {
        const checkAuth = async () => {
            const res = await fetch("http://localhost:5000/api/auth/main", {
                credentials: "include"
            });
            if (res.status === 401) {
                navigate("/Main"); 
            }
        };

        checkAuth();
        const interval = setInterval(checkAuth, 2000); // check every 2 sec
        return () => clearInterval(interval);
    }, [navigate]);

  return (
    <div className='loginPage'>
      <Navbar
        onSignUp={handleSignUp}
      />
      <h1 style={{ textAlign: "center" }}>Login</h1>
      <form onSubmit={handleSubmit}>
        <div className="box">
          <label htmlFor="username">Enter your username</label>
          <input name="username" placeholder='Username' onChange={handleChange} />

          <label htmlFor="email">Enter your email</label>
          <input type="email" name="email" placeholder='Email' onChange={handleChange} />

          <label htmlFor="password">Password</label>
          <input type="password" name="password" placeholder='Password' onChange={handleChange} />

          <button className='loginButton' type="submit">Submit</button>
          <p>{message}</p>
        </div>
      </form>
    </div>

  );
}

export default Login;
