import "../App.css";
import LoginSign from "../components/loginsign";
import { useEffect, useState } from "react";
import Navbar from "../components/navbar";
import { useContext } from "react";
import { GlobalFunctionContext } from "../context/functionsContext";
import { useNavigate } from "react-router-dom";
import Cookies from "js-cookie";
import axiosInstance from "../hooks/axios";

function Login() {
  const [form, setForm] = useState({ email: "", password: "" });
  const [message, setMessage] = useState("");
  const { handleSignUp, handleAbout } = useContext(GlobalFunctionContext);
  const { theme } = useContext(ThemeContext);
  

   const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // const handleLogin = async () => {
  //   try {
  //     const res = await axiosInstance.post("/", { email, password });
  //     const token = res.data.token;

  //     Cookies.set("authToken", token, { expires: 7 }); // expires in 7 days

  //     navigate("/Main");
  //   } catch (err) {
  //     console.error(err);
  //   }
  // };

  const navigate = useNavigate();

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });
useEffect(()=>{
  
})
  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/api/auth/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
      credentials: "include",
    });
    const data = await res.json();
    setMessage(data.msg);

    if (res.ok) {
      navigate("/Main");
    }
  };

  return (
    <div className="loginPage" style={{ background: theme.background, color: theme.text }}>
      <Navbar onSignUp={handleSignUp} />
      <h1 style={{ textAlign: "center" }}>Login</h1>
      <form onSubmit={handleSubmit}>
        <div className="box">
          <label htmlFor="username">Enter your username</label>
          <input
            name="username"
            placeholder="Username"
            onChange={handleChange}
          />

          <label htmlFor="email">Enter your email</label>
          <input
            type="email"
            name="email"
            placeholder="Email"
            onChange={handleChange}
          />

          <label htmlFor="password">Password</label>
          <input
            type="password"
            name="password"
            placeholder="Password"
            onChange={handleChange}
          />

          <button className="loginButton" type="submit">
            Submit
          </button>
          <p>{message}</p>
        </div>
      </form>
    </div>
  );
}

export default Login;
