import "../App.css";
import Myfooter from "../components/footer";
// import { products } from '../constants/c';
I
import { useNavigate } from "react-router-dom";
import {
  useCallback,
  useContext,
  useEffect,
  useMemo,
  useRef,
  useState,
} from "react";
import { LazyLoadImage } from "react-lazy-load-image-component";
import { useDispatch, useSelector } from "react-redux";
import {
  selectProducts,
  fetchProducts,
  selectLoading,
  selectError,
} from "../Slices/productsSlice";
import CustomButton from "../components/button";
import { ProductContext } from "../context/productContext";
import chevron from "../constants/chevron.png";
import searchImg from "../constants/searchImg.png";
import Navbar from "../components/navbar";
import MapComponent from "../components/mapComponent";
import MyMap from "../components/map";
import { GlobalFunctionContext } from "../context/functionsContext";
import { fetchWithAuth } from "../utils/fetchWithAuth";

function Main() {
  // const products = useSelector(selectProducts);
  const [search, setSearch] = useState("");

  const { selectedProduct: product } = useContext(ProductContext);
  const {
    navBarRef,
    handleLogin,
    handleSignUp,
    handleAbout,
    scrollToContact,
    scrollToUp,
    contactRef,
  } = useContext(GlobalFunctionContext);

  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  const navigate = useNavigate();

  // const dispatch = useDispatch();
  // const loading = useSelector(selectLoading);
  // const error = useSelector(selectError);

  // const fetchData = async () => {
  //   try {
  //     const res = await fetch("https://fakestoreapi.in/api/products")
  //     const data = await res.json()

  //     if (JSON.stringify(data.products) !== JSON.stringify(products)) {
  //       setProducts(data.products)
  //     }
  //     setLoading(false)
  //   }
  //   catch (err) {
  //     console.log("Failed to fetch products:", err);
  //     setLoading(false)
  //   }

  // }

  // fetchData()

  // useEffect(() => {
  //   dispatch(fetchProducts());
  // }, [dispatch]);

  useEffect(() => {
    const token = Cookies.get("token"); // read cookie
    if (!token) {
      navigate("/");
      return;
    }

    try {
      const decoded = jwtDecode(token);
      const now = Date.now() / 1000; // in seconds
      if (decoded.exp < now) {
        Cookies.remove("token"); // delete expired cookie
        navigate("/");
      }
    } catch (err) {
      console.error("Invalid token:", err);
      Cookies.remove("token");
      navigate("/");
    }
  }, [navigate]);

  const [newProduct, setNewProduct] = useState({
    name: "",
    price: "",
    description: "",
    category: "",
    image: "",
  });
  const handleDeleteProduct = async (id) => {
    try {
      await fetch(`http://localhost:5000/api/products/${id}`, {
        method: "DELETE",
        credentials: "include",
      });
      setProducts((prev) => prev.filter((product) => product._id !== id));
    } catch (err) {
      console.error("Error deleting product:", err);
    }
  };

  const handleAddProduct = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        credentials: "include",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newProduct),
      });
      const data = await res.json();
      console.log("Product added:", data);
      setProducts((prev) => [...prev, data.product]);
    } catch (err) {
      console.error("Error adding product:", err);
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/products", {
          credentials: "include",
        });
        const data = await res.json();
        setProducts(data);
        setLoading(false);
      } catch (err) {
        console.error("Failed to fetch products", err);
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const filteredProducts = useMemo(() => {
    return products.filter(
      (item) =>
        item.name.toLowerCase().includes(search.toLowerCase()) ||
        item.category.toLowerCase().includes(search.toLowerCase())
    );
  }, [search, products]);

  const { setSelectedProduct } = useContext(ProductContext);

  const handleProducts = (item) => {
    setSelectedProduct(item);
    navigate("/productPage");
  };

  // useEffect(() => {
  //   const checkAuth = async () => {
  //     try {
  //       const res = await fetchWithAuth(
  //         "http://localhost:5000/api/products",
  //         {},
  //         navigate
  //       );
  //       if (!res) return; // means logged out;

  //       if (res.status === 401) {
  //         navigate("/");
  //       }
  //     } catch (err) {
  //       console.error("Error checking auth:", err);
  //       navigate("/");
  //     }
  //   };

  //   checkAuth();
  //   const interval = setInterval(checkAuth, 2000);
  //   return () => clearInterval(interval);
  // }, [navigate]);

  return (
    <div className="app">
      <Navbar
        navRef={navBarRef}
        onLogin={handleLogin}
        onSignUp={handleSignUp}
        onAbout={handleAbout}
        onContact={scrollToContact}
      />
      <div>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            gap: "10px",
            padding: "10px 15px",
            background: "#f0f0f3",
            borderRadius: "12px",
            boxShadow: "4px 4px 10px #d1d1d1, -4px -4px 10px #ffffff",
            fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
            flexWrap: "wrap", // allows wrapping on small screens
          }}
        >
          {["Name", "Price", "Description", "Category", "Image URL"].map(
            (placeholder) => (
              <input
                key={placeholder}
                placeholder={placeholder}
                type={placeholder === "Price" ? "number" : "text"}
                style={{
                  padding: "6px 10px",
                  border: "none",
                  borderRadius: "8px",
                  background: "#e0e0e5",
                  boxShadow:
                    "inset 2px 2px 5px #d1d1d1, inset -2px -2px 5px #ffffff",
                  fontSize: "13px",
                  outline: "none",
                  width: "120px",
                  transition: "all 0.2s ease-in-out",
                }}
                onFocus={(e) =>
                  (e.target.style.boxShadow = "0 0 0 2px #007BFF")
                }
                onBlur={(e) =>
                  (e.target.style.boxShadow =
                    "inset 2px 2px 5px #d1d1d1, inset -2px -2px 5px #ffffff")
                }
                onChange={(e) =>
                  setNewProduct({
                    ...newProduct,
                    [placeholder.toLowerCase().replace(" ", "")]:
                      e.target.value,
                  })
                }
              />
            )
          )}

          <button
            onClick={handleAddProduct}
            style={{
              padding: "8px 12px",
              border: "none",
              borderRadius: "10px",
              background: "linear-gradient(90deg, #007BFF, #0056b3)",
              color: "#fff",
              fontSize: "14px",
              fontWeight: "600",
              cursor: "pointer",
              transition: "all 0.3s ease",
              boxShadow: "2px 2px 5px rgba(0,0,0,0.2)",
              whiteSpace: "nowrap",
            }}
            onMouseEnter={(e) =>
              (e.target.style.background =
                "linear-gradient(90deg, #0056b3, #007BFF)")
            }
            onMouseLeave={(e) =>
              (e.target.style.background =
                "linear-gradient(90deg, #007BFF, #0056b3)")
            }
          >
            Add
          </button>
        </div>
      </div>

      <div className="content">
        <h2>Products for Sale</h2>
        <div>
          <img className="searchImg" src={searchImg} alt="noimage" />
          <input
            type="search"
            className="search"
            name="search"
            id="search"
            placeholder="search anything"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </div>

        <div className="products">
          {loading ? (
            <div className="spinner"></div>
          ) : (
            filteredProducts.map((item) => (
              <div
                key={item.id}
                onClick={() => handleProducts(item)}
                className={`card ${item.discount > 10 ? "highlight" : ""}`}
              >
                <LazyLoadImage
                  className="imgProduct"
                  src={item.image}
                  alt="image cannot be found"
                />
                <a>
                  <h4> {item.name}</h4>
                </a>
                <strong>Price: ${item.price.toLocaleString()}</strong>
                {/* <p>Rating: {item.rating}</p> */}
                <div className="details">
                  <pre>Description: {item.description}</pre>
                  {/* <pre>Model: {item.model}</pre> */}
                  {/* <pre>Color: {item.color}</pre> */}
                  <pre>Category: {item.category}</pre>
                  {/* <pre>Sale: {item.onSale}</pre>
                  <pre>Discount: {item.discount}%</pre> */}
                </div>
                <button
                  className="btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDeleteProduct(item._id);
                  }}
                >
                  Delete
                </button>
              </div>
            ))
          )}
          <div onClick={scrollToUp}>
            <img className="img" src={chevron} alt="noimage" />
          </div>
        </div>
        <MyMap />
      </div>
      <Myfooter ref={contactRef} />
    </div>
  );
}

export default Main;
