import React, { useState, useContext } from "react";
import Navbar from "../components/navbar";
import { ProductContext } from "../context/productContext";
import { ThemeContext } from "../context/themeContext";
import { useNavigate } from "react-router-dom";

function CartScreen() {
  const { theme } = useContext(ThemeContext);
  const { selectedProduct: product } = useContext(ProductContext);
  const navigate = useNavigate();

  const [quantity, setQuantity] = useState(1);
  const total = product.price * quantity;

  return (
    <div>
      <Navbar />
      <div className="heading">
        <h1>My Cart</h1>
      </div>

      <div className="table">
        <table>
          <thead>
            <tr>
              <th>Product</th>
              <th>Price</th>
              <th>Quantity</th>
              <th>Total</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td className="mainTable">
                <div>
                  <strong>{product.name}</strong>
                  <p>{product.category}</p>
                </div>
              </td>
              <td>${product.price}</td>
              <td>
                <input
                  type="number"
                  min="1"
                  value={quantity}
                  onChange={(e) => setQuantity(Number(e.target.value))}
                  style={{ width: "60px", textAlign: "center" }}
                />
              </td>
              <td style={{ fontWeight: 700 }}>${total.toFixed(2)}</td>
            </tr>
          </tbody>
        </table>

        {/* Coupon & Checkout Section */}
        <div style={{ padding: "30px" }}>
          <label htmlFor="coupon">Enter your coupon</label>
          <input
            placeholder="Coupon"
            style={{ border: "2px solid black", borderRadius: "6px", marginLeft: "10px" }}
            type="text"
            name="coupon"
          />
          <div style={{ padding: "40px" }}>
            <h4>Cart Subtotal: ${total.toFixed(2)}</h4>
            <h4>Shipping: $5.00</h4>
            <hr />
            <h4>You Pay: ${(total + 5).toFixed(2)}</h4>
            <button
              style={{
                background: "#0097a3",
                borderRadius: "10px",
                padding: "10px",
                color: "white",
                marginTop: "15px",
              }}
              type="button"
              onClick={() => navigate("/checkout")}
            >
              Checkout
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CartScreen;
