import React, { useEffect } from "react";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { setCredentials } from "../Slices/authSlice";
import "../App.css";

const GitHubCallback = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    const handleGitHubCallback = async () => {
      const code = new URLSearchParams(window.location.search).get("code");
      if (!code) return;

      try {
        const res = await axios.post("http://localhost:5000/api/auth/github", { code });
        dispatch(setCredentials({ user: res.data.user, token: res.data.token }));
        navigate("/Main");
      } catch (err) {
        console.error("GitHub login error:", err);
      }
    };

    handleGitHubCallback();
  }, [dispatch, navigate]);

  return (
      <div style={styles.container}>
      <img src={githubLogo} alt="GitHub Logo" style={styles.logo} />
      <h2 style={styles.heading}>Logging you in with GitHub...</h2>
      <div className="spinner"></div>
      <p style={styles.text}>Please wait a moment while we authenticate your account.</p>
    </div>
  )
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    height: "80vh",
    background: "#f5f5f5",
    color: "#333",
    textAlign: "center",
    gap: "15px",
    fontFamily: "Arial, sans-serif",
  },
  logo: {
    width: "80px",
    height: "80px",
  },
  heading: {
    fontSize: "1.5rem",
  },
  text: {
    fontSize: "1rem",
    color: "#666",
  },
};


export default GitHubCallback;
