import React from "react";
import Navbar from "../components/navbar";
import Myfooter from "../components/footer";
import { ProductContext } from "../context/productContext";
import { ThemeContext } from "../context/themeContext";
import { useLocation, useNavigate } from "react-router-dom";
import { useContext } from "react";
import { LazyLoadImage } from "react-lazy-load-image-component";
import CustomButton from "../components/button";

function CartScreen() {
  const { theme } = useContext(ThemeContext);
  const navigate = useNavigate();
  const { selectedProduct: product } = useContext(ProductContext);
  const location = useLocation();
  //   const {product} = location.product

  return (
    <div>
      <Navbar />
      <div className="heading">
        <h1>My Cart</h1>
      </div>

      <div className="table">
        <table>
          <tr>
            <th>Product</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Total</th>
          </tr>
          <tr>
            <td className="mainTable">
              {/* <LazyLoadImage
                style={{ height: 30, width: 30, borderRadius: "8px" }}
                src={product.image}
                alt="no-image"
              /> */}
              <div>
                {/* <div style={{ fontWeight: 700 }}>{product.name}</div> */}
                {product.category}
              </div>
            </td>
            <td>${product.price}</td>
            <td>
              Quantity:
              <input type="range" name="quantity" />
            </td>
            <td style={{fontWeight:700}}>Total: {product.price}</td>
          </tr>
        </table>
        <div>
            <label htmlFor="coupon">Enter your coupon</label>
        <input placeholder="coupon" style={{border:"2px solid black", borderRadius:"6px"}} type="text" name="coupon" />
        <div style={{padding:"10px"}} >
            <h4>Cart Subtotal: </h4>
            <h4>Shipping</h4>
            <hr />
            <h4>You Pay: </h4>
            <button style={{background:"green", border}} type="button">Checkout</button>

        </div>
        </div>
      </div>

      {/* <Myfooter/> */}
    </div>
  );
}

export default CartScreen;
