import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import AdminNavbar from "../components/adminNavbar";
import { logout } from "../Slices/authSlice";
import { useLocation, useNavigate } from "react-router-dom";

function AdminDashboard() {
  const { token, user } = useSelector((state) => state.auth);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const location = useLocation();

  const [products, setProducts] = useState([]);
  const [users, setUsers] = useState([]);
  const [orders, setOrders] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: "",
    price: "",
    description: "",
    category: "",
    image: "",
    quantity: 1,
  });

  const handleLogout = () => {
    dispatch(logout());
    window.location.href = "/";
  };

  useEffect(() => {
    fetch("http://localhost:5000/api/products", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setProducts(Array.isArray(data) ? data : []))
      .catch((err) => console.error("Products fetch failed:", err));

    if (user.role === "admin") {
      fetch("http://localhost:5000/api/users", {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => res.json())
        .then((data) => setUsers(Array.isArray(data) ? data : []))
        .catch((err) => console.error("Users fetch failed:", err));
    }

    fetch("http://localhost:5000/api/orders", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setOrders(Array.isArray(data) ? data : []))
      .catch((err) => console.error("Orders fetch failed:", err));
  }, [token, user.role]);

  const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newProduct),
      });
      const data = await res.json();
      if (res.ok) {
        setProducts((prev) => [...prev, data.product || data]);
      }
      setNewProduct({
        name: "",
        price: "",
        description: "",
        category: "",
        image: "",
        quantity: 1,
      });
    } catch (err) {
      console.error("Add product failed:", err);
    }
  };

  const handleDeleteProduct = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/products/${id}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.ok) {
        setProducts(products.filter((p) => p._id !== id));
      }
    } catch (err) {
      console.error("Delete product failed:", err);
    }
  };

  const handleQuantityUpdate = async (id, quantity) => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/products/${id}/quantity`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ quantity }),
        }
      );
      if (res.ok) {
        setProducts(
          products.map((p) => (p._id === id ? { ...p, quantity } : p))
        );
      }
    } catch (err) {
      console.error("Update quantity failed:", err);
    }
  };

  const handleAssignRole = async (userId, role) => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/users/${userId}/role`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ role }),
        }
      );
      if (res.ok) {
        setUsers(users.map((u) => (u._id === userId ? { ...u, role } : u)));
      }
    } catch (err) {
      console.error("Assign role failed:", err);
    }
  };

  useEffect(() => {
    const handlePopState = () => {
      dispatch(logout());
      navigate("/");
    };
    window.addEventListener("popstate", handlePopState);
    return () => window.removeEventListener("popstate", handlePopState);
  }, [navigate, location]);

  if (!user || (user.role !== "admin" && user.role !== "subadmin")) {
    return (
      <p
        style={{
          textAlign: "center",
          marginTop: "50px",
          fontSize: "20px",
          fontWeight: "bold",
          color: "#e74c3c",
        }}
      >
        🚫 Access Denied
      </p>
    );
  }

  // Reusable styles
  const cardStyle = {
    background: "white",
    padding: "20px",
    marginBottom: "20px",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
  };

  const tableStyle = {
    width: "100%",
    borderCollapse: "collapse",
    marginTop: "15px",
  };

  const thtdStyle = {
    border: "1px solid #ddd",
    padding: "10px",
    textAlign: "center",
  };

  return (
    <div
      style={{
        background: "#f4f6f9",
        minHeight: "100vh",
        padding: "20px",
        fontFamily: "Arial, sans-serif",
      }}
    >
      <AdminNavbar />

      <div style={{ marginTop: "20px" }}>
        {/* Products */}
        <section style={cardStyle}>
          <h2>📦 Products</h2>
          <form
            onSubmit={handleAddProduct}
            style={{
              display: "flex",
              flexWrap: "wrap",
              gap: "10px",
              marginTop: "15px",
            }}
          >
            <input
              type="text"
              placeholder="Name"
              value={newProduct.name}
              onChange={(e) =>
                setNewProduct({ ...newProduct, name: e.target.value })
              }
              style={{ flex: "1", padding: "8px" }}
              required
            />
            <input
              type="number"
              placeholder="Price"
              value={newProduct.price}
              onChange={(e) =>
                setNewProduct({ ...newProduct, price: e.target.value })
              }
              style={{ flex: "1", padding: "8px" }}
              required
            />
            <input
              type="text"
              placeholder="Category"
              value={newProduct.category}
              onChange={(e) =>
                setNewProduct({ ...newProduct, category: e.target.value })
              }
              style={{ flex: "1", padding: "8px" }}
              required
            />
            <input
              type="text"
              placeholder="Image URL"
              value={newProduct.image}
              onChange={(e) =>
                setNewProduct({ ...newProduct, image: e.target.value })
              }
              style={{ flex: "1", padding: "8px" }}
            />
            <input
              type="number"
              placeholder="Quantity"
              value={newProduct.quantity}
              onChange={(e) =>
                setNewProduct({ ...newProduct, quantity: e.target.value })
              }
              min={1}
              style={{ flex: "1", padding: "8px" }}
            />
            <button
              type="submit"
              style={{
                background: "#3498db",
                color: "white",
                border: "none",
                padding: "10px 15px",
                borderRadius: "6px",
                cursor: "pointer",
              }}
            >
              ➕ Add Product
            </button>
          </form>

          <table style={tableStyle}>
            <thead>
              <tr>
                <th style={thtdStyle}>Name</th>
                <th style={thtdStyle}>Price ($)</th>
                <th style={thtdStyle}>Category</th>
                <th style={thtdStyle}>Quantity</th>
                <th style={thtdStyle}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {products.map((p) => (
                <tr key={p._id}>
                  <td style={thtdStyle}>{p.name}</td>
                  <td style={thtdStyle}>{p.price}</td>
                  <td style={thtdStyle}>{p.category}</td>
                  <td style={thtdStyle}>
                    <input
                      type="number"
                      defaultValue={p.quantity || 0}
                      onBlur={(e) =>
                        handleQuantityUpdate(p._id, e.target.value)
                      }
                      style={{ width: "60px", padding: "5px" }}
                    />
                  </td>
                  <td style={thtdStyle}>
                    <button
                      onClick={() => handleDeleteProduct(p._id)}
                      disabled={user.role === "subadmin"}
                      style={{
                        background: "#e74c3c",
                        color: "white",
                        border: "none",
                        padding: "6px 10px",
                        borderRadius: "6px",
                        cursor: "pointer",
                      }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>

        {/* Users */}
        {user.role === "admin" && (
          <section style={cardStyle}>
            <h2>👥 Users</h2>
            <table style={tableStyle}>
              <thead>
                <tr>
                  <th style={thtdStyle}>Name/Email</th>
                  <th style={thtdStyle}>Role</th>
                  <th style={thtdStyle}>Assign Role</th>
                </tr>
              </thead>
              <tbody>
                {users.map((u) => (
                  <tr key={u._id}>
                    <td style={thtdStyle}>{u.name || u.email}</td>
                    <td style={thtdStyle}>{u.role}</td>
                    <td style={thtdStyle}>
                      <select
                        value={u.role}
                        onChange={(e) =>
                          handleAssignRole(u._id, e.target.value)
                        }
                        style={{ padding: "5px", borderRadius: "6px" }}
                      >
                        <option value="user">User</option>
                        <option value="subadmin">Sub-admin</option>
                        <option value="admin">Admin</option>
                      </select>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </section>
        )}

        {/* Orders */}
        <section style={cardStyle}>
          <h2>🛒 Orders</h2>
          <table style={tableStyle}>
            <thead>
              <tr>
                <th style={thtdStyle}>User</th>
                <th style={thtdStyle}>Total ($)</th>
                <th style={thtdStyle}>Items</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((o) => (
                <tr key={o._id}>
                  <td style={thtdStyle}>{o.user?.name || "Unknown"}</td>
                  <td style={thtdStyle}>{o.total}</td>
                  <td style={thtdStyle}>
                    {o.items?.map((i) => i.name).join(", ")}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>
      </div>
    </div>
  );
}

export default AdminDashboard;
