import React, { useState, useContext } from "react";
import { ProductContext } from "../context/productContext";

function Dashboard() {
  const { setProducts } = useContext(ProductContext);

  const [newProduct, setNewProduct] = useState({
    name: "",
    price: "",
    description: "",
    category: "",
    image: "",
  });

  const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        credentials: "include",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newProduct),
      });

      if (!res.ok) throw new Error("Failed to add product");

      const data = await res.json();
      setProducts((prev) => [...prev, data]);
        dispatch(addProduct(D));

      setNewProduct({
        name: "",
        price: "",
        description: "",
        category: "",
        image: "",
      });
    } catch (err) {
      console.error("Error adding product:", err);
    }
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "#f8f9fb",
        display: "flex",
        justifyContent: "center",
        alignItems: "flex-start",
        padding: "40px 20px",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      <div
        style={{
          width: "100%",
          maxWidth: "600px",
          background: "#fff",
          borderRadius: "16px",
          boxShadow: "0 4px 15px rgba(0,0,0,0.1)",
          padding: "30px",
        }}
      >
        <h2
          style={{
            textAlign: "center",
            marginBottom: "20px",
            color: "#333",
          }}
        >
          ðŸ“¦ Add New Product
        </h2>

        <form
          onSubmit={handleAddProduct}
          style={{
            display: "flex",
            flexDirection: "column",
            gap: "18px",
          }}
        >
          {[
            { label: "Product Name", key: "name", type: "text" },
            { label: "Price", key: "price", type: "number" },
            { label: "Description", key: "description", type: "text" },
            { label: "Category", key: "category", type: "text" },
            { label: "Image URL", key: "image", type: "text" },
          ].map((field) => (
            <div key={field.key} style={{ display: "flex", flexDirection: "column" }}>
              <label
                style={{
                  fontSize: "14px",
                  fontWeight: "600",
                  marginBottom: "6px",
                  color: "#444",
                }}
              >
                {field.label}
              </label>
              <input
                type={field.type}
                value={newProduct[field.key]}
                onChange={(e) =>
                  setNewProduct({ ...newProduct, [field.key]: e.target.value })
                }
                required
                style={{
                  padding: "10px",
                  borderRadius: "8px",
                  border: "1px solid #ccc",
                  fontSize: "14px",
                  outline: "none",
                  transition: "border 0.2s",
                }}
                onFocus={(e) => (e.target.style.border = "1px solid #007BFF")}
                onBlur={(e) => (e.target.style.border = "1px solid #ccc")}
              />
            </div>
          ))}

          <button
            type="submit"
            style={{
              marginTop: "10px",
              padding: "12px",
              border: "none",
              borderRadius: "10px",
              background: "linear-gradient(90deg, #007BFF, #0056b3)",
              color: "#fff",
              fontSize: "16px",
              fontWeight: "600",
              cursor: "pointer",
              transition: "background 0.3s ease",
              boxShadow: "2px 2px 6px rgba(0,0,0,0.2)",
            }}
            onMouseEnter={(e) =>
              (e.target.style.background =
                "linear-gradient(90deg, #0056b3, #007BFF)")
            }
            onMouseLeave={(e) =>
              (e.target.style.background =
                "linear-gradient(90deg, #007BFF, #0056b3)")
            }
          >
            âž• Add Product
          </button>
        </form>
      </div>
    </div>
  );
}

export default Dashboard;
