import './App.css';
import { Routes, Route } from 'react-router-dom';

import React, { lazy, Suspense } from 'react';
import store from './components/store';
import { Provider } from 'react-redux';
import { PersistGate } from 'redux-persist/integration/react';
import { persistor } from './components/store';
import axiosInstance from './hooks/axios';
import { ProductProvider } from './context/productContext';
import { GlobalFunctionProvider } from './context/functionsContext';
import { fun } from './screens/SignUp';
import { ThemeProvider } from './context/themeContext';

const Main = lazy(() => import('./screens/Main'));
const About = lazy(() => import('./screens/about'));
const ProductPage = lazy(() => import('./screens/productPage'));
const Login = lazy(() => import('./screens/Login'));
const SignUp = lazy(() => import('./screens/SignUp'));
global.axios = axiosInstance;


function App() {
  useEffect(() => {
  const token = Cookies.get("token");
  if (!token || isTokenExpired(token)) {
    Cookies.remove("token");
    // Redirect to login
    navigate("/login");
  }
}, []);
  return (
    <ThemeProvider>
    <GlobalFunctionProvider>
      <ProductProvider>
        <Provider store={store}>
          <Suspense fallback={<div>Loading page...</div>}>
            <PersistGate loading={null} persistor={persistor}>
              <Routes>
                <Route path='/Main' element={<Main />} />
                <Route path='/About' element={<About />} />
                <Route path='/ProductPage' element={<ProductPage />} />
                <Route path='/' element={<Login />} />
                <Route path='/SignUp' element={<SignUp />} loader={()=>{
                    fun
                }} />
              </Routes>
            </PersistGate>
          </Suspense>
        </Provider>
      </ProductProvider>
    </GlobalFunctionProvider>
    </ThemeProvider>
  );
}

export default App;
