import React, { useState, useContext, useEffect } from "react";
import { ProductContext } from "../context/productContext";
import { useDispatch, useSelector } from "react-redux";
import { addProduct } from "../Slices/productsSlice";
import Navbar from "../components/navbar";
function Dashboard() {
  const {token, user} = useSelector((state)=> state.auth);
  const [products, setProducts] = useState([]);
  const [users, setUsers] = useState([]);
  const [orders, setOrders] = useState([])
  const [newProduct, setNewProduct] = useState({
    name:"",
    price:"",
    description:"",
    category:"",
    image:"",
    quantity:1
  });
  // const { setProducts } = useContext(ProductContext);

  const dispatch = useDispatch()

  if (!user || (user.role!=="admin" && user.role!=="subadmin")) {
    return <p style={{textAlign:"center"}}>Access Denied</p>
  }

  useEffect(()=>{
    fetch("http://localhost:5000/api/products", {
      headers:{Authorization: `Bearer ${token}`},
    }).then((res)=>res.json()).then(setProducts)

    if (user.role=="admin") {
      fetch("http://localhost:5000/api/users", {
        headers:{Authorization:`Bearer ${token}`}
      }).then((res)=> res.json()).then(setUsers)}

      fetch("http://localhost:5000/api/orders", 
        {
          headers:{Authorization:`Bearer ${token}`}
        }
      ).then((res)=> res.json()).then(setOrders)

  }, [token, user.role])

  const handleAddProduct = async (e) =>{
    e.preventDefault();

    try{
      const res = await fetch("http://localhost:5000/api/products", {
        method:"POST",
        headers:{
          "Content-Type":"applications/json",
          Authorization: `Bearer ${token}`
          },
          body:JSON.stringify(newProduct),
        }
      );
      const data = await res.json();
      if(res.ok) setProducts((prev)=> [...prev, data]);
      setNewProduct({name:"", price:"", description:"", category:"", image:"", quantity:1});
    }catch(err){
      console.error("Add product failed", err);
    }
  }

  const handleDeleteProduct = async() =>{
    await fetch(`http://localhost:5000/api/products/${id}`, {
      method:"DELETE",
      headers:{
        Authorization:`Bearer ${token}`
      },
    });
    setProducts(products.filter((p)=> p._id !== id));
  };

  const handleUpdateQuantity = async(id, quantity) => {
    await fetch(`http://localhost:5000/api/products/${id}`,{
      method:"PUT",
      headers:{Authorization:`Bearer ${token}`}
    })
  }

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "#f8f9fb",
        display: "flex",
        justifyContent: "center",
        alignItems: "flex-start",
        padding: "40px 20px",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      <div
        style={{
          width: "100%",
          maxWidth: "600px",
          background: "#fff",
          borderRadius: "16px",
          boxShadow: "0 4px 15px rgba(0,0,0,0.1)",
          padding: "30px",
        }}
      >
        <h2
          style={{
            textAlign: "center",
            marginBottom: "20px",
            color: "#333",
          }}
        >
          ðŸ“¦ Add New Product
        </h2>

        <form
          onSubmit={handleAddProduct}
          style={{
            display: "flex",
            flexDirection: "column",
            gap: "18px",
          }}
        >
          {[
            { label: "Product Name", key: "name", type: "text" },
            { label: "Price", key: "price", type: "number" },
            { label: "Description", key: "description", type: "text" },
            { label: "Category", key: "category", type: "text" },
            { label: "Image URL", key: "image", type: "text" },
          ].map((field) => (
            <div key={field.key} style={{ display: "flex", flexDirection: "column" }}>
              <label
                style={{
                  fontSize: "14px",
                  fontWeight: "600",
                  marginBottom: "6px",
                  color: "#444",
                }}
              >
                {field.label}
              </label>
              <input
                type={field.type}
                value={newProduct[field.key]}
                onChange={(e) =>
                  setNewProduct({ ...newProduct, [field.key]: e.target.value })
                }
                required
                style={{
                  padding: "10px",
                  borderRadius: "8px",
                  border: "1px solid #ccc",
                  fontSize: "14px",
                  outline: "none",
                  transition: "border 0.2s",
                }}
                onFocus={(e) => (e.target.style.border = "1px solid #007BFF")}
                onBlur={(e) => (e.target.style.border = "1px solid #ccc")}
              />
            </div>
          ))}

          <button
            type="submit"
            style={{
              marginTop: "10px",
              padding: "12px",
              border: "none",
              borderRadius: "10px",
              background: "linear-gradient(90deg, #007BFF, #0056b3)",
              color: "#fff",
              fontSize: "16px",
              fontWeight: "600",
              cursor: "pointer",
              transition: "background 0.3s ease",
              boxShadow: "2px 2px 6px rgba(0,0,0,0.2)",
            }}
            onMouseEnter={(e) =>
              (e.target.style.background =
                "linear-gradient(90deg, #0056b3, #007BFF)")
            }
            onMouseLeave={(e) =>
              (e.target.style.background =
                "linear-gradient(90deg, #007BFF, #0056b3)")
            }
          >
            âž• Add Product
          </button>
        </form>
      </div>
    </div>
  );
}

export default Dashboard;
