import React from "react";
import "../App.css";
import { useNavigate } from "react-router-dom";
import CustomButton from "../components/button";
import { useContext } from "react";
import { ThemeContext } from "../context/themeContext";

const Navbar = ({ onLogin, onSignUp, onAbout, onContact, navRef, handleLogout }) => {
  const { theme, toggleTheme, isDark } = useContext(ThemeContext);
  const navigate = useNavigate();

  return (
    <div
      ref={navRef}
      className="navbar"
      style={{ background: theme.navbarBg, color: theme.text }}
    >
      <div className="brand">
        <a onClick={() => navigate("/")}>
          <img className="logo" src="/logo192.png" alt="no-image" />
        </a>
        <h3>React Commerce</h3>
      </div>
      <ul className="pages">
        <li onClick={() => navigate("/")}>
          <a>Home</a>
        </li>
        <li onClick={onContact}>
          <a>Contact</a>
        </li>
        <li onClick={onAbout}>
          <a>About</a>
        </li>
        <li>
          <CustomButton label="Login" onClick={onLogin} />
        </li>
        <li>
          <CustomButton label="Sign Up" onClick={onSignUp} />
        </li>
        <button
          onClick={toggleTheme}
          style={{
            background: theme.buttonBg,
            color: theme.buttonText,
            border: "none",
            padding: "2px 2px",
            borderRadius: "5px",
            margin: "2px",
            cursor: "pointer",
          }}
        >
          {isDark ? "Light Mode" : "Dark Mode"}
        </button>
        <button
          onClick={handleLogout}
          style={{
            padding: "8px 14px",
            background: "crimson",
            color: "white",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
          }}
        >
          Logout
        </button>
      </ul>
    </div>
  );
};

export default React.memo(Navbar);
