import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { logout } from "../Slices/authSlice";

const AdminDashboard = () => {
  const { user } = useSelector((state) => state.auth);
  const dispatch = useDispatch();

  if (!user) {
    return <p style={{ textAlign: "center", marginTop: "20px" }}>No user logged in</p>;
  }

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "#f4f6f9",
        padding: "20px",
        boxSizing: "border-box",
      }}
    >
      <div
        style={{
          background: "#fff",
          borderRadius: "16px",
          boxShadow: "0 6px 20px rgba(0,0,0,0.1)",
          padding: "30px",
          maxWidth: "900px",
          width: "100%",
          overflow: "hidden",
        }}
      >
        <h1
          style={{
            fontSize: "28px",
            textAlign: "center",
            marginBottom: "20px",
            color: "#333",
          }}
        >
          Admin Dashboard
        </h1>

        {/* User Info */}
        <div
          style={{
            marginBottom: "20px",
            padding: "20px",
            borderRadius: "12px",
            background: "#f9fafc",
            border: "1px solid #eee",
          }}
        >
          <h2 style={{ fontSize: "20px", marginBottom: "10px" }}>Welcome, {user.username}</h2>
          <p style={{ margin: "5px 0", color: "#666" }}>Email: {user.email}</p>
          <p style={{ margin: "5px 0", color: "#666" }}>Role: {user.role}</p>
        </div>

        {/* Actions */}
        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            gap: "15px",
            justifyContent: "center",
          }}
        >
          <button
            onClick={() => dispatch(logout())}
            style={{
              padding: "12px 20px",
              borderRadius: "8px",
              border: "none",
              background: "#e63946",
              color: "#fff",
              fontSize: "16px",
              cursor: "pointer",
              transition: "0.3s",
            }}
            onMouseOver={(e) => (e.target.style.background = "#d62828")}
            onMouseOut={(e) => (e.target.style.background = "#e63946")}
          >
            Logout
          </button>

          <button
            style={{
              padding: "12px 20px",
              borderRadius: "8px",
              border: "none",
              background: "#457b9d",
              color: "#fff",
              fontSize: "16px",
              cursor: "pointer",
              transition: "0.3s",
            }}
            onMouseOver={(e) => (e.target.style.background = "#1d3557")}
            onMouseOut={(e) => (e.target.style.background = "#457b9d")}
          >
            Manage Users
          </button>

          <button
            style={{
              padding: "12px 20px",
              borderRadius: "8px",
              border: "none",
              background: "#2a9d8f",
              color: "#fff",
              fontSize: "16px",
              cursor: "pointer",
              transition: "0.3s",
            }}
            onMouseOver={(e) => (e.target.style.background = "#21867a")}
            onMouseOut={(e) => (e.target.style.background = "#2a9d8f")}
          >
            View Reports
          </button>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
