import '../App.css';
import LoginSign from '../components/loginsign';
import { useState } from 'react';
import Navbar from '../components/navbar';
import { useContext } from 'react';
import { GlobalFunctionContext } from '../context/functionsContext';
import { useNavigate } from 'react-router-dom';
import jwtDecode from ""
function Login() {
  const [form, setForm] = useState({ email: "", password: "" });
  const [message, setMessage] = useState("");
  const { handleSignUp, handleAbout } = useContext(GlobalFunctionContext)

  const navigate = useNavigate();
  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/api/auth/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
      credentials: "include"
    });
    const data = await res.json();
    setMessage(data.msg);
  
    if (res.ok) {
    navigate("/Main");

    setTimeout(async () => {
        await fetch("http://localhost:5000/api/auth/logout", {
            method: "POST",
            credentials: "include"
        });
        navigate("/");
    }, 5000);
}
const data1 = await res.json();
    if (res.ok) {
      // Get token from cookie by hitting a backend endpoint or returning in JSON
      const tokenRes = await fetch("http://localhost:5000/api/auth/token", {
        credentials: "include"
      });
      const { token } = await tokenRes.json();

      const decoded = jwtDecode(token);
      const expiry = decoded.exp * 1000; // convert to ms

      // Auto logout at expiry
      setTimeout(() => {
        fetch("http://localhost:5000/api/auth/logout", { method: "POST", credentials: "include" });
        window.location.href = "/login";
      }, expiry - Date.now());

      // Navigate to main page
      window.location.href = "/main";
    } else {
      setMessage(data1.msg);
    }

  };

  return (
    <div className='loginPage'>
      <Navbar
        onSignUp={handleSignUp}
      />
      <h1 style={{ textAlign: "center" }}>Login</h1>
      <form onSubmit={handleSubmit}>
        <div className="box">
          <label htmlFor="username">Enter your username</label>
          <input name="username" placeholder='Username' onChange={handleChange} />

          <label htmlFor="email">Enter your email</label>
          <input type="email" name="email" placeholder='Email' onChange={handleChange} />

          <label htmlFor="password">Password</label>
          <input type="password" name="password" placeholder='Password' onChange={handleChange} />

          <button className='loginButton' type="submit">Submit</button>
          <p>{message}</p>
        </div>
      </form>
    </div>

  );
}

export default Login;
