import '../App.css';
import Myfooter from '../components/footer';
// import { products } from '../constants/c';
import { useNavigate } from 'react-router-dom';
import { useCallback, useContext, useEffect, useMemo, useRef, useState } from 'react';
import { LazyLoadImage } from 'react-lazy-load-image-component';
import { useDispatch, useSelector } from 'react-redux';
import { selectProducts, fetchProducts, selectLoading, selectError } from '../Slices/productsSlice';
import CustomButton from '../components/button';
import { ProductContext } from '../context/productContext';
import chevron from '../constants/chevron.png';
import Navbar from '../components/navbar';

function Main() {
  const products = useSelector(selectProducts);
  const [search, setSearch] = useState("")

  // const [products, setProducts] = useState([]);
  // const [loading, setLoading] = useState(true)

  const navigate = useNavigate()
  const contactRef = useRef(null)
  const navBarRef = useRef(null)
  const dispatch = useDispatch()
  const loading = useSelector(selectLoading)
  const error = useSelector(selectError)


  const postProduct = async (newProduct) => {
    try {
      const response = await fetch("https://fakestoreapi.in/api/products", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newProduct),
      });

      const data = await response.json();
      console.log("Product added:", data);
    } catch (error) {
      console.error("Error posting product:", error);
    }
  };
  // postProduct()


  // const fetchData = async () => {
  //   try {
  //     const res = await fetch("https://fakestoreapi.in/api/products")
  //     const data = await res.json()

  //     if (JSON.stringify(data.products) !== JSON.stringify(products)) {
  //       setProducts(data.products)
  //     }
  //     setLoading(false)
  //   }
  //   catch (err) {
  //     console.log("Failed to fetch products:", err);
  //     setLoading(false)
  //   }

  // }

  // fetchData()

  useEffect(() => {
    dispatch(fetchProducts())
  }, [dispatch])

  const filteredProducts = useMemo(() => {
    return products.filter((item) => item.title.toLowerCase().includes(search.toLowerCase()))
  },
    [search, products])

  const scrollToContact = useCallback(() => {
    contactRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [])

  const scrollToUp = () => {
    navBarRef.current?.scrollIntoView({ behavior: 'smooth' })
  }

  const handleLogin = useCallback(() => {
    navigate('/Login')
  }, [navigate])

  const handleSignUp = useCallback(() => {
    navigate('/SignUp')
  }, [navigate])

  const handleAbout = useCallback(() => {
    navigate('/About')
  }, [navigate])

  const { setSelectedProduct } = useContext(ProductContext)

  const handleProducts = (item) => {
    setSelectedProduct(item)
    navigate('/productPage')
  }


  return (
    <div className='app'>
      <Navbar
        navRef={navBarRef}
        onLogin={handleLogin}
        onSignUp={handleSignUp}
        onAbout={handleAbout}
        onContact={scrollToContact}
      />
      <div className="content">
        <h2>Products for Sale</h2>
        <input type="search" className='search' name="search" id="search" placeholder='search anything' value={search} onChange={(e) => setSearch(e.target.value)} />
        <div className='products'>
          {
            loading ? (
              <div className="spinner"></div>

            ) : (filteredProducts.map(item =>
              <div key={item.id} onClick={() => handleProducts(item)} className={`card ${item.discount > 10 ? 'highlight' : ''}`}
              >
                <LazyLoadImage className='imgProduct' src={item.image} alt="image cannot be found" />
                <a><h4> {item.title}</h4></a>
                <strong>Price: ${item.price.toLocaleString()}</strong>
                <p>Rating: {item.rating}</p>
                <div className='details'>
                <pre>{item.brand}</pre>
                <pre>{item.model}</pre>
                <pre>{item.color}</pre>
                <pre>{item.category}</pre>
                <pre>{item.onSale}</pre>
                <pre>Discount: {item.discount}%</pre>
                </div>
              </div>
            ))
          }
          <div onClick={scrollToUp}>
            <img className='img' src={chevron} alt="noimage" />
          </div>
        </div>
      </div>
      <Myfooter ref={contactRef} />
    </div>
  );
}

export default Main;
